        public static void SeedLoop(ApiRecipe[] recipeArray, RecipeDbContext context)
        {
            foreach (var item in recipeArray)
            {
                var recipe = new Recipe();
                recipe.Label = item.label;
                recipe.Image = item.image;
                recipe.IsAdded = false;
                recipe.Source = item.source;
                recipe.TotalWeight = item.totalWeight;
                recipe.Url = item.url;
                recipe.Calories = item.calories;
                
                foreach (var healthItem in item.healthLabels)
                {
                    recipe.HealthLabel.Add(new HealthLabel
                    {
                        StringValue = healthItem
                    });
                }


                foreach (var nutritionItem in item.nutritionInfo)
                {
                    recipe.Nutritioninfo.Add(new Nutritioninfo
                    {
                        Label = nutritionItem.label,
                        Total = nutritionItem.total
                    });
                }
                foreach (var ingredientItem in item.ingredients)
                {
                    recipe.Ingredient.Add(new Ingredient
                    {
                        Text = ingredientItem.text,
                        Weight = ingredientItem.weight
                    });
                }
                context.Add(recipe);
            }
            context.SaveChanges();
        }
